[ExcludeFromCodeCoverage(Justification = "Stubbed out data, will be replaced eventually")]
public class RemosService : IRemosService
{
    private readonly IReadOnlyCollection<GeneralCertificateAttachment> _existingDocumentAttachments = CreateMockGeneralCertificateDocuments();

    public Task<GeneralCertificateAttachment> GetGeneralCertificateAttachmentByIdAsync(
        string gcId,
        string documentId,
        CancellationToken cancellationToken = default)
    {
        var attachments = _existingDocumentAttachments
            .Where(x => x.GeneralCertificateId.Equals(gcId, StringComparison.OrdinalIgnoreCase) && x.GeneralCertificateDocumentId == documentId);

        return Task.FromResult(attachments.FirstOrDefault());
    }

